CREATE FUNCTION getTactic()
  RETURNS text
  LANGUAGE plpgsql
  AS $$
DECLARE
  id TEXT = '';
BEGIN
  DELETE FROM prev_tactic WHERE TRUE;
  SELECT tactic_id INTO id FROM tactic ORDER BY RANDOM() LIMIT 1;
  INSERT INTO prev_tactic (tactic_id) VALUES (id);
  RETURN id;
END
$$;

